--[[	Title: StreamHandler	Handles input and output streams.]]--- Includes the datastream library.require("datastream")--- Handles the input that client sent.function streamReceive( sender, handler, id, encoded, decoded )	Msg("\nWLC command by '"..sender:Nick().."': ")	Msg(decoded.command)	if table.Count(decoded.args) > 0 then		for key, value in pairs( decoded.args ) do			Msg(" " .. value)		end	end	Msg("\nOutput: ")	result = checkInput( sender, decoded.command, decoded.args )		if table.Count(result) == 0 then		result = handleInput( sender, decoded.command, decoded.args )	end	streamSend( sender, "wlcServer", result );		for key, value in pairs( result ) do		print(value)	endenddatastream.Hook( "wlcClient", streamReceive )--- Send the output to the client.function streamSend( receiver, data ) 		datastream.StreamToClients( receiver, "wlcServer", data );end