--[[	Title: StreamHandler	Handles input and output streams.]]--- Includes the datastream library.require("datastream")--- Handles and prints the output that the server generated.function streamReceive( umsg )				print(umsg:ReadString())endusermessage.Hook( "wlcServer", streamReceive )--- Send the command to the server.function streamSend( data ) 		datastream.StreamToServer( "wlcClient", data );end