--[[	Title: StreamHandler	Handles input and output streams.]]--- Includes the datastream library.require("datastream")--- Handles and prints the output that the server generated.function streamReceive( handler, id, encoded, decoded )  	print("1: "..handler)	print("2: "..id)	print("3: "..encoded)	print("4: "..decoded)				if table.Count(decoded) > 0 then		if decoded.action == "wlcGuiShow" then			print("Showing GUI.")			wlcGuiShow()		else			for key, value in pairs( decoded ) do				print(value)			end		end	else		print("Error - No output returned.")	endenddatastream.Hook( "wlcServer", streamReceive )--- Send the command to the server.function streamSend( data ) 		datastream.StreamToServer( "wlcClient", data );end